import React, { createContext, useEffect, useReducer } from 'react'


const INIT_USER = {
  loading: false,
  username: JSON.parse(localStorage.getItem('username')) || null,
  error: null
}

export const AuthContext = createContext(INIT_USER)


function loginReducer(state, action) {
  switch (action.type) {
    case "LOGIN_START":
      return {
        loading: true,
        username: null,
        error: undefined
      }
    case "LOGIN_SUCCESS":
      return {
        loading: false,
        username: action.payload,
        error: undefined
      }
    case "LOGIN_FAILED":
      return {
        loading: false,
        username: null,
        error: action.payload
      }
    case "LOGOUT":
      return {
        loading: null,
        username: null,
        error: undefined
      }
    default:
      return state;
  }
}


export const creteAuthContext = createContext(INIT_USER);

const AuthContextProvider = ({ children }) => {

  const [state, dispatch] = useReducer(loginReducer, INIT_USER);

  useEffect(() => {
    localStorage.setItem('user', JSON.stringify(state.user))
  }, [state.user])

  return (
    <AuthContext.Provider value={{ loading: state.loading, username: state.username, error: state.error, dispatch }}>
      {children}
    </AuthContext.Provider>
  )
}

export default AuthContextProvider
