import Opening from '../../components/opening/Opening'
import Sidebar from '../../components/sidebar/Sidebar'
import './home.css'
import { AuthContext } from '../../AuthContext'
import { useContext, useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom'
import axios from 'axios'
import jwt_decode from 'jwt-decode'


const Home = () => {
  const [token, setToken] = useState('');
  const [expired, setExpired] = useState('');
  const { userData } = useContext(AuthContext);
  const navigate = useNavigate();

  const getToken = async () => {
    try {
      const refreshToken = await axios.get('http://localhost:5500/5R2I/auth/token');
      setToken(refreshToken.data.aksesToken);
      const decoded = jwt_decode(refreshToken.data.aksesToken);
      setExpired(decoded.exp);
    } catch (error) {
      console.log(error);
    }
  }

  useEffect(() => {
    userData == null && navigate('/langsung')
    getToken();
    console.log('isi token refresh: ', getToken);
  }, [userData, navigate])

  return (

    <div className='container'>
      <div className='wrapper'>
        <Sidebar />
        <Opening />
      </div>
    </div>
  )
}

export default Home
